import React, {useState} from 'react';
import './Channel.css';
//For transforming markdown to html
import unified from 'unified'
import parse from 'remark-parse'
import remark2react from 'remark-react'

import MessageSend from '../MessageSend/MessageSend'

//Moment.js library - deprecated
//it's slow
//it's heavey
//it's mutable ?
//it's hard to debug
//uncomment the following to use moment
/*
const moment = require('moment');
let val;
val = moment().format('h:mm, DD-MM-YYYY');
*/

//using Luxon instead of Moment.js
const {DateTime} = require('luxon');



//Change Fake Channel with {channel.name}



//list of channels
var channel = {
    name: 'Fake channel',
};

const Channel = () => {

    const [messages, setMessages] = useState([])

    const addMessage = (message) => {
        setMessages([
          ...messages,
          message
        ])
    }



    return(
        <div className="channel">



            <MessageSend addMessage={addMessage} />

        </div>
    );
}

export {channel};
export default Channel;
